import React, { useState } from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import {
  TextField,
  Typography,
  Button,
  Grid,
  Box,
  TextareaAutosize,
} from "@material-ui/core";
import SendIcon from "@material-ui/icons/Send";

const useStyles = makeStyles((theme) => ({
  mainContainer: {
    background: "#3E3E3E",
    height: "100vh",
  },
  form: {
    borderBottom: "0px!important",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    position: "absolute",
  },
  button: {
    marginTop: "1rem",
    color: "#7395AE",
    borderColor: "#7395AE",
  },
  heading: {
    color: "#7395AE",
    textAlign: "center",
    textTransform: "uppercase",
  },
}));

const InputField = withStyles({
  root: {
    "& label.Mui-focused": {
      color: "#7395AE",
    },
    "& label": {
      color: "#B1A296",
    },
    "& .MuiOutlinedInput-root": {
      "& fieldset": {
        borderColor: "#B1A296",
      },
      "&:hover fieldset": {
        borderColor: "#B1A296",
      },
      "&.Mui-focused fieldset": {
        borderColor: "#B1A296",
      },
    },
  },
})(TextField);

export default class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.submitForm = this.submitForm.bind(this);
    this.state = {
      status: "",
    };
  }

  render() {
    const { status } = this.state;
    return (
      <>
        <form
          onSubmit={this.submitForm}
          action="https://formspree.io/xrgydyag"
          method="POST"
        >
          <label>Email:</label>
          <input type="email" name="email" />
          <label>Message:</label>
          <input type="text" name="message" />
          {status === "SUCCESS" ? <p>Thanks!</p> : <button>Submit</button>}
          {status === "ERROR" && <p>Ooops! There was an error.</p>}
        </form>
        <form
          onSubmit={this.submitForm}
          action="https://formspree.io/xrgydyag"
          method="POST"
        >
          <InputField
            style={{ borderBottom: "0px!important" }}
            multiline={false}
            fullWidth={true}
            label="Name"
            type="text"
            name="name"
            variant="outlined"
            inputProps={{ style: { color: "white" } }}
            margin="dense"
            size="medium"
          />
          <br />

          <InputField
            multiline={false}
            fullWidth={true}
            label="Email"
            type="text"
            name="email"
            variant="outlined"
            inputProps={{ style: { color: "white" } }}
            margin="dense"
            size="medium"
          />
          <br />

          <InputField
            multiline={true}
            rows="3"
            rowsMax="20"
            fullWidth={true}
            label="Message"
            type="text"
            name="message"
            variant="outlined"
            inputProps={{ style: { color: "white" } }}
            margin="dense"
            size="medium"
          ></InputField>

          <br />
          {status === "SUCCESS" ? (
            <p>Thanks!</p>
          ) : (
            <Button
              className={classes.button}
              variant="outlined"
              fullWidth={true}
              endIcon={<SendIcon />}
            >
              contact me
            </Button>
          )}
          {status === "ERROR" && <p>Ooops! There was an error.</p>}
        </form>
      </>
    );
  }

  submitForm(ev) {
    ev.preventDefault();
    const form = ev.target;
    const data = new FormData(form);
    const xhr = new XMLHttpRequest();
    xhr.open(form.method, form.action);
    xhr.setRequestHeader("Accept", "application/json");
    xhr.onreadystatechange = () => {
      if (xhr.readyState !== XMLHttpRequest.DONE) return;
      if (xhr.status === 200) {
        form.reset();
        this.setState({ status: "SUCCESS" });
      } else {
        this.setState({ status: "ERROR" });
      }
    };
    xhr.send(data);
  }
}
