import React from "react";
import Navbar from "./Navbar";
import { makeStyles } from "@material-ui/core/styles";
import Carousel from "react-material-ui-carousel";
// import funfinancial from "../Videos/funfinancial";
import flightguru from "../Videos/flightguru.gif";
import {
  Box,
  Grid,
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography,
} from "@material-ui/core";

const useStyles = makeStyles({
  mainContainer: {
    background: "#3E3E3E",
    height: "100%",
  },
});

const Portfolio = () => {
  const classes = useStyles();

  const gifs = [
    {
      name: "Fun Financial",
      description:
        "An App to teach your kids how to manage credit from a young age. JavaScript | ReactJS/Hooks | Ruby | Ruby on Rails | PostgreSQL",
      image: "../Videos/funfinancial.gif",
    },
    {
      name: "Flight Guru",
      description:
        "An app to find restaurants at your terminal. Ruby | Ruby on Rails | API integration | PostgreSQL",
      image: { flightguru },
    },
  ];
  return (
    <>
      <Box component="div" className={classes.mainContainer}>
        <Navbar />
        <Carousel></Carousel>
      </Box>
    </>
  );
};

export default Portfolio;
